func TestCalSha(t *testing.T) {
	ticketStr := "{\"ID\":\"Voter_1967564789_何俭涛\",\"CandidateNum\":0,\"NameAndOption\":{\"何俭涛\":\"\",\"徐许越\":\"\",\"李为君\":\"\",\"李文豪\":\"\",\"李林轩\":\"\",\"沈文涛\":\"\",\"熊琦\":\"\",\"胡靛青\":\"\"},\"RSAPublicKey\":\"\",\"Signature\":\"WiNH2BnCyxP6r2GU8vumYdtZS+U/GxhdlKVV1IQf2WIHDdbBsWI82WaT/GGW3ILWeUCzFo29QeemskB6f/8JiRDoG7NZ8bujuKpIWyINmfzPkhJKUq3c1Fe+2iZJ1f39i9pPu14e5IS0qRz/ExyT/3k+YlBIOEoAkManlZj0TsM=\"}"
	ticket := VoteUtils.BallotTicket{}
	err := json.Unmarshal([]byte(ticketStr), &ticket)
	if err != nil {
		fmt.Println("转换为Ticket失败:", err)
	}
	fmt.Println(ticket)
	block := Block{}
	block.InitBlock(true, "0", ticket)
	fmt.Println(block)
	block.CalPostSha(20)
}
